import React, { useState, forwardRef, useImperativeHandle } from "react";

import styles from "./toast.module.scss";

const Toast = forwardRef((props, ref) => {
  const [value, setValue] = useState(false);

  const showToast = () => {
    setValue(true);
  };

  const hideToast = () => {
    setValue(false);
  };

  useImperativeHandle(ref, () => {
    return {
      showToast: showToast
    };
  });

  return (
    <div className={`${styles.wrap} ${value ? styles.show : null}`} {...props}>
      <div className={styles.closeBtn} onClick={hideToast}>
        Hide
      </div>
      <h1>Child component</h1>
      <h3>
        Component State: <span>{value.toString()}</span>
      </h3>
    </div>
  );
});

export default Toast;

